			dis[i] = Integer.MAX_VALUE;
		}
		dis[c1] = 0;
		nums[c1] = 1;
		w[c1] = weight[c1];

		/******************************************/

		for(int i = 0; i < n; i++) {
			int u = -1, min = Integer.MAX_VALUE;
