					min = dis[j];
				}
			}
			if(u == -1) break;
			visit[u] = true;
			for(int v = 0; v < n; v++) {
				if(!visit[v] && e[u][v] != Integer.MAX_VALUE) {
					if(dis[u] + e[u][v] < dis[v]) {
						 dis[v] = dis[u] + e[u][v];
						 nums[v] = nums[u];
